function checkAll(ele) {
  var checkboxes = document.getElementsByTagName('input');
  if (ele.checked) {
      for (var i = 0; i < checkboxes.length; i++) {
          if (checkboxes[i].type == 'checkbox') {
              checkboxes[i].checked = true;
          }
      }
  } else {
      for (var i = 0; i < checkboxes.length; i++) {
          if (checkboxes[i].type == 'checkbox') {
              checkboxes[i].checked = false;
          }
      }
  }
}

function changeDates(obj1){


  var selected_index = obj1.selectedIndex;
  var selected_text = obj1.options[selected_index].text;

  var obj2 = document.getElementById("dateSelect");
  var jsonData = document.getElementById("variableJSON");
  var subject = JSON.parse(jsonData.text);
  obj2.options.length=0; 
  var opt = "";
	for(var i=0; i<=subject.dates.length; i++)
	{
    opt = document.createElement('option');
    var str = subject.dates[i];
    var parts = str.slice(0, -1).split('T');
    var dateComponent = parts[0];
    
    opt.text=dateComponent;
		obj2.add(opt);
	}
  };
